predicate far_or_equal(var int:x1, var int:y1,
                       var int:x2, var int:y2)=
          man_dist(x1,y1,x2,y2) >= 4 \/ 
         (x1 = x2 /\ y1 = y2);

function var int: man_dist(var int:u1, 
         var int:v1, var int:u2, var int:v2) =
         abs(u1 - u2) + abs(v1 - v2);

var 0..4: a;  var 0..4: b;  var 0..4: c;  var 0..4: d;  
constraint far_or_equal(a,b,c,d);

solve satisfy;

array[1..15] of var 0..1: b;
array[1..4] of var 1..4: x;

include "alldifferent.mzn";
constraint alldifferent(x);
constraint sum(x) <= 9;

solve  
       :: int_search(b ++ x, first_fail, indomain_min, complete)
   %   :: int_search(b ++ x, dom_w_deg, indomain_min, complete)
      satisfy;

int: n;
set of int: PERSON = 1..n;
int: T; % number of tables
set of int: TABLE = 1..T;
int: S; % tables size
array[int,1..2] of PERSON: couples;
set of PERSON: key;

array[TABLE] of var set of PERSON: table;
array[PERSON] of var TABLE: seat;


predicate not_same_table(PERSON:p1, PERSON: p2) =
                        seat[p1] != seat[p2];

constraint global_cardinality_low_up(seat, [t|t in TABLE], [0|t in TABLE], [S|t in TABLE]);

constraint forall(c in index_set_1of2(couples))
                 (not_same_table(couples[c,1],couples[c,2]));

constraint forall(p1,p2 in key where p1 < p2)
                 (not_same_table(p1,p2));

var int: obj = sum(c in index_set_1of2(couples))(seat[couples[c,1]] + seat[couples[c,2]]);

constraint forall(t in TABLE, p in PERSON)(p in table[t] <-> seat[p] = t);    

solve minimize obj;

output [show(table), " = ", show(obj)];


n = 20;
T = 5;
S = 5;
couples = [| 1, 2 | 4, 5 | 6, 7 | 8, 10
           | 11, 12 | 13, 14 | 17, 18 |];
key = {1, 6, 7, 8};

% Langfords problem (m,n) m copies of digits 1..n
% where any two consecutive copies of digit k
% are separated by k other digits
int: n;
set of int: DIG = 1..n;
int: m;
set of int: COPY = 1..m;
int: l = m*n;
set of int: POS = 1..l;
array[DIG,COPY] of var POS: x;

include "alldifferent.mzn";
constraint alldifferent([x[d,c] | d in DIG, c in COPY]);

constraint forall(d in DIG, c in 1..m-1)
                 (x[d,c+1] = x[d,c] + d + 1);

solve satisfy;

output [show(x),"\n"]
       ++ [ if fix(x[d,c]) = p then show(d) ++ " " else "" endif
          | p in POS, d in DIG, c in COPY ]
       ;

% setselect revised
int: n; % number of elements
int: u; % cardinality of chosen set
int: k; % number of subsets
array[1..k] of set of 1..n: s;

array[1..u] of var 1..n: x;
%constraint forall(i in 1..u-1)(x[i] < x[i+1]);
constraint forall(i,j in 1..u where i < j)(x[i] != x[j]);

constraint forall(i in 1..k)(sum(j in 1..u)(x[j] in s[i]) <= 1);

solve maximize sum(x);

output ["x = ", show(x), ";\n"];

var int: B;
var int: C;
var int: D;

constraint 2.0*B + 2.0*C + 1.0*D <= 30.0;
constraint 1.5*B + 2.0*C + 3.0*D <= 25.0;
constraint 2.0*B + 1.0*C + 1.0*D <= 20.0;
solve maximize 3.0*B + 1.0*C + 3.0*D;

constraint B >= 0 /\ C >= 0 /\ D >= 0

int: n; % number of rooms
set of int: ROOM = 1..n;
int: m; % number of rectangle/offsets
set of int: ROFF = 1..m;
array[ROFF,1..4] of int: d; % defns
set of int: ROT = 1..4;
array[ROOM,ROT] of set of ROFF: shape;
int: h; % height of roll
int: maxl; % maximum length of roll

n = 3; m = 7;
d = [| 0,0,3,4  % (xoffset,yoffset,xsize,ysize)
    | 0,1,4,3
    | 1,4,1,1
    | 3,1,1,2
    | 4,2,1,1
    | 1,0,2,1 
    | 0,0,4,3 |];
shape = [| {1,3,4}, {2,5,6}, {}, {}
         | {1,3,4}, {2,5,6}, {}, {}
         | {1}, {7}, {}, {}  |];
h = 7; maxl = 12;

array[ROOM] of var 0..maxl: x;
array[ROOM] of var 0..h: y;
array[ROOM] of var ROT: rot;

var 0..maxl: l; % length of carpet used

solve minimize l;

constraint forall(i in ROOM)(shape[i,rot[i]] != {});

constraint forall(i in ROOM)(forall(r in ROFF)
  (r in shape[i,rot[i]] -> 
  (x[i] + d[r,1] + d[r,3] <= l /\
   y[i] + d[r,2] + d[r,4] <= h)));

constraint forall(i,j in ROOM where i < j)
  (forall(r1,r2 in ROFF)
 (r1 in shape[i,rot[i]] /\ 
  r2 in shape[j,rot[j]] -> 
(x[i] + d[r1,1] + d[r1,3] <= x[j] + d[r2,1]
                   \/
 x[j] + d[r2,1] + d[r2,3] <= x[i] + d[r1,1]
                   \/
 y[i] + d[r1,2] + d[r1,4] <= y[j] + d[r2,2]
                   \/
y[j] + d[r2,2] + d[r2,4] <= y[i] + d[r1,2])
   ));

output ["l = \(l);\nx = \(x);\ny = \(y);\nrot = \(rot);\n"];

array[0..2,0..2] of var 0..2: x;
constraint sum(i in 0..2)(x[i,i]) <= 1;
constraint x[x[1,1],1] = 2;

solve satisfy;

int: n;
set of int: JOB = 1..n;
int: m;
set of int: MACH = 1..m;
set of int: TASK = 1..m;
array[JOB,TASK] of int:  d;  % length of task
array[JOB,TASK] of MACH: mc; % which machine

int: maxt = sum(j in JOB, t in TASK)(d[j,t]);

array[JOB,TASK] of var 0..maxt: s; % start time

% performs tasks in a job in order
constraint forall(j in JOB, t in 1..m-1)
                 (s[j,t] + d[j,t] <= s[j,t+1]);

include "disjunctive.mzn";

constraint forall(ma in MACH)
                 ( let { array[int] of var int: ss = [ s[j,t] | j in JOB, t in TASK where mc[j,t] = ma ];
                         array[int] of int:     dd = [ d[j,t] | j in JOB, t in TASK where mc[j,t] = ma ]; } in
                   disjunctive(ss,dd));


var 0..maxt: makespan = max(j in JOB)(s[j,m] + d[j,m]);

solve  
%      ::
%      seq_search([ int_search([s[i,j]| i in JOB, j in TASK],
%                             smallest, indomain_min, complete),
%                  int_search([makespan], input_order, indomain_min, complete)])
%      seq_search([ int_search([makespan], input_order, indomain_min, complete),
%                   int_search([s[i,j]| i in JOB, j in TASK],
%                             smallest, indomain_min, complete)])
         minimize makespan;

output [ "makespan = ", show(makespan), "\n"] ++
       [ show_int(3,s[j,t]) ++ if t = m  then "\n" else " " endif
       | j in JOB, t in TASK];

int: i = 3; int: j = 3;
var 0..5: x; var 0..2: y; var 0..3: z;
