int: W; % number of Warehousse
set of int: WARE = 1..W;
int: F; % number of Factories
set of int: FACT = 1..F;

array[WARE] of int: demand;
array[FACT] of int: production;

array[FACT,WARE] of int: cost;

array[FACT,WARE] of var int: ship;

%int: m = max(demand);
%array[FACT,WARE] of var 0..m: ship;
constraint forall(f in FACT, w in WARE)(ship[f,w] >= 0);

constraint forall(w in WARE)
                 (sum(f in FACT)(ship[f,w]) >= demand[w]);

constraint forall(f in FACT)
                 (sum(w in WARE)(ship[f,w]) <= production[f]);

solve minimize sum(f in FACT, w in WARE)
                  (cost[f,w]*ship[f,w]);


output [ show_int(2,ship[f,w]) ++
         if w == W then "\n" else " " endif
       | f in FACT, w in WARE ];

% setselect revised
int: n; % number of elements
int: u; % cardinality bound of chosen set
int: k; % number of subsets
array[1..k] of set of 1..n: s;

array[1..u] of var 0..n: x;
constraint forall(i in 1..u-1)(x[i] >= (x[i] != 0) + x[i+1]);

constraint forall(i in 1..k)(sum(j in 1..u)(x[j] in s[i]) <= 1);

solve maximize sum(x);

output ["x = ", show(x), ";\n"];

var -4..4:x;
var -4..4:y;
constraint divp(x,y,3);

predicate divp(var int:x,var int: y,var int: z) 
     = assert(lb(y) >= 0,
              "divp: y must be non-negative",
              let { var 0..ub(y)-1: r } in
	        x = z * y + r /\ r < y);

solve satisfy;

int: n; % points to be clustered
set of int: POINT = 1..n;
array[POINT,POINT] of int: dist; % distance between two points
int: maxdist = max([ dist[i,j] | i,j in POINT]);


int: k; % number of clusters
set of int: CLUSTER = 1..k;

int: maxdiam;

array[POINT] of var CLUSTER: x;

constraint forall(i,j in POINT where i < j /\ x[i] = x[j])
                 (dist[i,j] <= maxdiam);

constraint forall(i in 1..k-1)
                 (  min([ j | j in POINT where x[j] = i])
                  < min([ j | j in POINT where x[j] = i+1]));

var 0..maxdist: obj = min( i,j in POINT where i < j )
                         ( dist[i,j] + maxdist * (x[i] != x[j]) );

include "value_precede_chain.mzn";
constraint value_precede_chain([ i | i in 1..k ],x);

solve maximize obj;

output ["obj = \(obj); x = \(x)\n"];

int: n;
set of int: PERSON = 1..n;
int: T; % number of tables
set of int: TABLE = 1..T;
int: S; % tables size
array[int,1..2] of PERSON: couples;
set of PERSON: key;

array[TABLE] of var set of PERSON: table;

predicate not_same_table(PERSON:p1, PERSON: p2) =
          forall(t in TABLE)(not ({p1,p2} subset table[t]));

constraint forall(t in TABLE)(card(table[t]) <= S);

constraint forall(p in PERSON)(exists(t in TABLE)(p in table[t]));

constraint forall(c in index_set_1of2(couples))
                 (not_same_table(couples[c,1],couples[c,2]));

constraint forall(p1,p2 in key where p1 < p2)
                 (not_same_table(p1,p2));

var int: obj = sum(c in index_set_1of2(couples))
                  ( let { var TABLE: tn1 = sum(t in TABLE)(t * bool2int(couples[c,1] in table[t]));
                          var TABLE: tn2 = sum(t in TABLE)(t * bool2int(couples[c,2] in table[t]));
                    } in  tn1 + tn2);



solve minimize obj;

output [show(table), " = ", show(obj)];


