                 (let {var int: on = sum(n in NURSE)
                                        (bool2int(x[n,d]=night))        
                      } in l <= on /\ on <= u);

solve satisfy;

output [ code[fix(x[n,d])] 
         ++ if d = m then "\n" else " " endif
       | n in NURSE, d in DAY];

